import axios from 'axios'
import React from 'react'
import toast from 'react-hot-toast'
import { useNavigate } from 'react-router-dom';

export default function TarjetaBoost({Boost}) {
    const navigate = useNavigate();
    const token=localStorage.getItem("TokenUsuario")
    let idusuarioconectado = localStorage.getItem("IdUsuario")
    let id_cliente = Boost.id_cliente

    const eliminarcuenta = (_id,tid) =>{
        
        axios.delete(`/eloboosts/${_id}`,{ headers: {"x-access-token" : `${token}`} })
        .then(res=>{
            console.log(res)
            if(res.status===204){
                toast.success("Eliminado exitosamente")
                toast.dismiss(tid)
            }
            navigate('/cuentas')
        })
        .catch(error=>{
            if(error.response.status===404){
                toast.error("No existe la cuenta")
            }else if(error.response.data.message==="jwt expired"){
                toast.error("SesiÃ³n Cerrada vuelve a Loggearte")
                localStorage.clear()
                navigate("/login")
            }else{
                toast.error(error.response.data)
            }
        })
    }
    const handleDelete = (_id) =>{
        toast((t) =>(
            <div className='text-xl text-white'>
                <p>Â¿Desea eliminar la cuenta? <strong>{_id}</strong></p>
                <br></br>
                <div>
                    <button className='bg-red-500 hover:bg-red-500 px-3 py-2 rounded-sm mx-2' onClick={()=>eliminarcuenta(_id,t.id)}>Eliminar</button>
                    <button className='bg-slate-400 hover:bg-slate-500 px-3 py-2 text-white rounded-sm mx-2' onClick={()=>{toast.dismiss(t.id)}}>Cancelar</button>
                </div>
            </div>
        ),{
            style: {
                background: "#202020"
            }
        })
    }
    if(id_cliente!==idusuarioconectado){
        console.log("pep"+id_cliente+"/"+idusuarioconectado)
  return (
    <>
    <div className='bg-zinc-800 text-white rounded-sm shadow-sm shadow-black
    hover:bg-zinc-700 hover:cursor-pointer'>
    <div className='px-4 py-7'>
      <div className='flex justify-between'>
      <h4>{Boost._id}</h4>
      </div>
      <h1>{Boost.id_vendedor}</h1>
      <p>{Boost.liga_inicio}</p>
      <p>{Boost.liga_deseada}</p>
      <p>{Boost.precio}â¬</p>
      <div>
        <button className='bg-orange-500 text-xl px-2 py-1 rounded-sm hover:bg-orange-400' onClick={()=>{alert("Comprado")}}>Boostear</button>
      </div>
    </div>
    </div>
    </>
  )
    }else{
        console.log(id_cliente+"/"+idusuarioconectado)
        return (
            <>
            <div className='bg-zinc-800 text-white rounded-sm shadow-sm shadow-black
            hover:bg-zinc-700 hover:cursor-pointer'>
            <div className='px-4 py-7'>
              <div className='flex justify-between'>
              <h4>{Boost._id}</h4>
              </div>
              <h1>{Boost.id_vendedor}</h1>
            <p>{Boost.liga_inicio}</p>
            <p>{Boost.liga_deseada}</p>
              <div>
                <button className='bg-red-700 text-xl px-2 py-1 rounded-sm hover:bg-red-400' onClick={()=>handleDelete(Boost._id)}>Eliminar</button>
                <button className='bg-blue-800 text-xl px-2 py-1 rounded-sm hover:bg-blue-400' onClick={()=>{navigate(`/actualizarcuenta/${Boost._id}`)}}>Editar</button>
              </div>
            </div>
            </div>
            </>
          )
        }
}
