import React, {useEffect} from 'react'
import axios from 'axios'
import * as Yup from 'yup'
import { useNavigate } from 'react-router-dom'
import toast from 'react-hot-toast'
import { Formik, Form, Field, ErrorMessage } from 'formik'
export default function Login() {
  const navigate = useNavigate()
  useEffect(() => {
    if(localStorage.getItem("IdUsuario")||localStorage.getItem("TokenUsuario")){
      navigate("/")
    }
  }, []); 

  return (
    <div className='divBox flex items-center justify-center h-screen w-screen'>
      <Formik
      initialValues={{
        email: '',
        contraseña: ''
      }}
      validationSchema={Yup.object({
        email: Yup.string().required("Es obligatorio poner el correo para iniciar sesion").email("Ha de ser un correo"),
        contraseña: Yup.string().required("Has de poner una contraseña para iniciar sesion")
      })
    }
    onSubmit={({values}, actions) =>{
      console.log(values)
      axios.post('/login',{values})
      .then(res=>console.log(res))
      .catch(error=> console.log(error))
      
    }}
      >
        {({handleSubmit}) =>(
          <Form onSubmit={handleSubmit}>
            <div>
              <h1 className='text-xl px-2 py-2 justify-center'>Login</h1>
            <div>
            <label>Correo Electronico</label><br></br>
            <Field name="email" placeholder="CorreoDeEjemplo@gmail.com"/><br></br>
            <ErrorMessage name='email' className='text-red-900' component="p"/>
            </div>
           
            <div>
            <label>Contraseña Usuario</label><br></br>
            <Field name="contraseña" type="password"/><br></br>
            <ErrorMessage name='contraseña' className='text-red-900' component="p"/>
            </div>
            
            <button type='submit' className='text-xl bg-blue-500'>Iniciar sesion</button>
            </div>
          </Form>
      )}
      </Formik>
    </div>
  )
}
